// src/RecordingsList.js

import React, { useState, useEffect, useCallback } from 'react';
import {
  Card,
  Table,
  Button,
  Alert,
  Badge,
  Spinner,
  Modal
} from 'react-bootstrap';
import axios from 'axios';
import './App.css';      // your custom CSS
import './design.css';   // design system CSS

const RecordingsList = ({ refreshKey, apiUrl }) => {
  const [recordings, setRecordings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selected, setSelected] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const fetchRecordings = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await axios.get(`${apiUrl}/api/recordings`, { timeout: 10000 });
      setRecordings(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error('Error fetching recordings:', err);
      if (err.response) {
        setError(`Server error: ${err.response.status} – ${err.response.data.message || 'Unknown'}`);
      } else {
        setError('Unable to load recordings. Check your connection.');
      }
    } finally {
      setLoading(false);
    }
  }, [apiUrl]);

  useEffect(() => {
    fetchRecordings();
  }, [fetchRecordings, refreshKey]);

  const formatSize = bytes => {
    if (!bytes) return '0 Bytes';
    const k = 1024, sizes = ['Bytes','KB','MB','GB'];
    const i = Math.floor(Math.log(bytes)/Math.log(k));
    return `${(bytes/Math.pow(k,i)).toFixed(2)} ${sizes[i]}`;
  };

  const formatDate = str => {
    const d = new Date(str);
    return d.toLocaleString('en-US', {
      year:'numeric',month:'short',day:'numeric',
      hour:'2-digit',minute:'2-digit'
    });
  };

  const getName = fn => {
    const parts = fn.split('-');
    if (parts.length>=2) {
      const t = parseInt(parts[1],10), d = new Date(t);
      if (!isNaN(d)) {
        return `Recording ${d.toLocaleDateString()} ${d.toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'})}`;
      }
    }
    return fn.replace('.webm','');
  };

  const openModal = rec => setSelected(rec) || setShowModal(true);
  const closeModal = () => setShowModal(false) || setSelected(null);

  const download = rec => {
    const url = `${apiUrl}/api/recordings/${rec.id}`;
    const a = document.createElement('a');
    a.href = url;
    a.download = rec.filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const total = recordings.reduce((sum,r) => sum + (r.size||0), 0);

  if (loading) {
    return (
      <Card className="main-card">
        <Card.Header className="card-header-custom">
          <h4>📁 My Recordings</h4>
        </Card.Header>
        <Card.Body className="text-center py-5">
          <Spinner animation="border" /> Loading…
        </Card.Body>
      </Card>
    );
  }

  return (
    <>
      <Card className="main-card">
        <Card.Header className="card-header-custom d-flex justify-content-between align-items-center">
          <div>
            <h4>📁 My Recordings</h4>
            <small>Manage your screen recordings</small>
          </div>
          <div>
            <Badge bg="secondary" className="me-2">
              {recordings.length} recording{recordings.length!==1?'s':''}
            </Badge>
            <Button
              variant="outline-light"
              size="sm"
              onClick={fetchRecordings}
            >
              🔄 Refresh
            </Button>
          </div>
        </Card.Header>
        <Card.Body className="p-0">
          {error && (
            <Alert variant="warning" className="m-3">
              <strong>Connection Issue</strong><br/>
              {error}<br/>
              <Button variant="outline-warning" size="sm" onClick={fetchRecordings}>
                🔄 Try Again
              </Button>
            </Alert>
          )}
          {!error && recordings.length===0 && (
            <Alert variant="info" className="m-3 text-center">
              📹 No recordings yet. Record and upload to see them here.
            </Alert>
          )}
          {recordings.length>0 && !error && (
            <div className="table-responsive">
              <Table hover className="mb-0">
                <thead className="table-dark">
                  <tr>
                    <th>Recording</th>
                    <th>Status</th>
                    <th>Size</th>
                    <th>Created</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {recordings.map(rec => (
                    <tr key={rec.id}>
                      <td>
                        <strong>{getName(rec.filename)}</strong><br/>
                        <small className="text-muted">{rec.filename}</small>
                      </td>
                      <td><Badge bg="success">Uploaded</Badge></td>
                      <td>{formatSize(rec.size)}</td>
                      <td>{formatDate(rec.created_at)}</td>
                      <td>
                        <Button
                          variant="outline-primary"
                          size="sm"
                          onClick={()=>openModal(rec)}
                        >▶️</Button>{' '}
                        <Button
                          variant="outline-success"
                          size="sm"
                          onClick={()=>download(rec)}
                        >📥</Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
                <tfoot className="table-light">
                  <tr>
                    <td colSpan={3}><strong>Total Storage:</strong></td>
                    <td colSpan={2}>{formatSize(total)}</td>
                  </tr>
                </tfoot>
              </Table>
            </div>
          )}
        </Card.Body>
      </Card>

      <Modal show={showModal} onHide={closeModal} size="lg" centered>
        <Modal.Header closeButton>
          <Modal.Title>
            🎬 {selected ? getName(selected.filename) : ''}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className="p-0 bg-dark">
          {selected && (
            <video
              src={`${apiUrl}/api/recordings/${selected.id}`}
              controls autoPlay
              style={{ width:'100%' }}
            />
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeModal}>
            Close
          </Button>
          <Button variant="primary" onClick={()=>download(selected)}>
            📥 Download
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default RecordingsList;
