// src/RecordingsList.js

import React, { useState, useEffect, useCallback } from 'react';
import {
  Card,
  Table,
  Button,
  Alert,
  Badge,
  Spinner,
  Modal
} from 'react-bootstrap';
import axios from 'axios';
import './App.css';       // your styles
import './design.css';    // your design system

const RecordingsList = ({ refreshKey, apiUrl }) => {
  const [recordings, setRecordings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedRecording, setSelectedRecording] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Fetch recordings from backend
  const fetchRecordings = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`${apiUrl}/api/recordings`, {
        timeout: 10000
      });
      setRecordings(Array.isArray(response.data) ? response.data : []);
    } catch (err) {
      console.error('Error fetching recordings:', err);
      if (err.response) {
        setError(`Server error: ${err.response.status} ‚Äì ${err.response.data.message || 'Unknown'}`);
      } else {
        setError('Failed to load recordings. Check your connection.');
      }
    } finally {
      setLoading(false);
    }
  }, [apiUrl]);

  // Effect to reload on mount or when refreshKey/apiUrl change
  useEffect(() => {
    fetchRecordings();
  }, [fetchRecordings, refreshKey]);

  // Helpers
  const formatFileSize = bytes => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes','KB','MB','GB'];
    const i = Math.floor(Math.log(bytes)/Math.log(k));
    return `${(bytes/Math.pow(k,i)).toFixed(2)} ${sizes[i]}`;
  };

  const formatDate = dateString => {
    const date = new Date(dateString);
    return date.toLocaleString('en-US', {
      year: 'numeric', month: 'short',
      day: 'numeric', hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getFileName = filename => {
    const parts = filename.split('-');
    if (parts.length>=2) {
      const ts = parseInt(parts[1],10);
      const d = new Date(ts);
      if (!isNaN(d.getTime())) {
        return `Recording ${d.toLocaleDateString()} ${d.toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'})}`;
      }
    }
    return filename.replace('.webm','');
  };

  // Modal controls
  const playRecording = rec => {
    setSelectedRecording(rec);
    setShowModal(true);
  };
  const closeModal = () => {
    setShowModal(false);
    setSelectedRecording(null);
  };

  const downloadRecording = rec => {
    const url = `${apiUrl}/api/recordings/${rec.id}`;
    const a = document.createElement('a');
    a.href = url;
    a.download = rec.filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  // Total storage used
  const totalSize = recordings.reduce((sum,r) => sum + (r.size||0), 0);

  // Render
  if (loading) {
    return (
      <Card className="main-card">
        <Card.Header className="card-header-custom">
          <h4 className="card-title">üìÅ My Recordings</h4>
        </Card.Header>
        <Card.Body className="card-body-custom text-center">
          <Spinner animation="border" className="spinner-custom mb-3"/>
          <p className="loading-text">Loading your recordings‚Ä¶</p>
        </Card.Body>
      </Card>
    );
  }

  return (
    <>
      <Card className="main-card">
        <Card.Header className="card-header-custom">
          <div className="header-content">
            <div>
              <h4 className="card-title">üìÅ My Recordings</h4>
              <p className="card-subtitle">Manage and view your screen recordings</p>
            </div>
            <div className="header-actions">
              <Badge className="count-badge">
                {recordings.length} recording{recordings.length!==1?'s':''}
              </Badge>
              <Button
                className="btn-custom btn-secondary btn-sm"
                onClick={fetchRecordings}
              >
                üîÑ Refresh
              </Button>
            </div>
          </div>
        </Card.Header>

        <Card.Body className="card-body-custom p-0">
          {error && (
            <Alert variant="danger" className="m-4">
              <strong>Connection Issue</strong><br/>
              {error}<br/>
              <Button variant="outline-light" size="sm" onClick={fetchRecordings}>
                üîÑ Try Again
              </Button>
            </Alert>
          )}

          {!error && recordings.length===0 ? (
            <div className="empty-state">
              <div className="empty-icon">üìπ</div>
              <h5>No recordings yet</h5>
              <p>Start by recording your screen, then upload to see it here!</p>
            </div>
          ) : (
            <div className="table-container">
              <Table className="table-custom" hover>
                <caption className="table-caption">
                  A list of your recent screen recordings
                </caption>
                <thead className="table-header">
                  <tr>
                    <th>Recording</th>
                    <th>Status</th>
                    <th>Size</th>
                    <th>Created</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {recordings.map(rec => (
                    <tr key={rec.id}>
                      <td>
                        <strong>{getFileName(rec.filename)}</strong><br/>
                        <small className="text-muted">{rec.filename}</small>
                      </td>
                      <td>
                        <Badge bg="success">‚úÖ Uploaded</Badge>
                      </td>
                      <td>{formatFileSize(rec.size)}</td>
                      <td>{formatDate(rec.created_at)}</td>
                      <td>
                        <Button
                          className="btn-action btn-play"
                          size="sm"
                          onClick={()=>playRecording(rec)}
                        >‚ñ∂Ô∏è</Button>{' '}
                        <Button
                          className="btn-action btn-download"
                          size="sm"
                          onClick={()=>downloadRecording(rec)}
                        >üì•</Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
                {recordings.length>0 && (
                  <tfoot className="table-footer">
                    <tr>
                      <td colSpan={3}><strong>Total Storage:</strong></td>
                      <td colSpan={2}>{formatFileSize(totalSize)}</td>
                    </tr>
                  </tfoot>
                )}
              </Table>
            </div>
          )}
        </Card.Body>
      </Card>

      <Modal show={showModal} onHide={closeModal} size="lg" centered>
        <Modal.Header closeButton>
          <Modal.Title>
            üé¨ {selectedRecording ? getFileName(selectedRecording.filename) : ''}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className="p-0">
          {selectedRecording && (
            <video
              src={`${apiUrl}/api/recordings/${selectedRecording.id}`}
              controls
              autoPlay
              style={{ width:'100%' }}
            />
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeModal}>Close</Button>
          <Button variant="primary" onClick={()=>downloadRecording(selectedRecording)}>
            üì• Download
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default RecordingsList;
