import React, { useState, useRef, useEffect } from 'react';
import { Button, Container, Row, Col, Card, Alert, Badge, ProgressBar, Breadcrumb, Dropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import RecordingsList from './RecordingsList';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const App = () => {
  // Recording state
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordedChunks, setRecordedChunks] = useState([]);
  const [recordingUrl, setRecordingUrl] = useState(null);

  // UI state
  const [uploadStatus, setUploadStatus] = useState('');
  const [refreshKey, setRefreshKey] = useState(0);
  const [recordingTime, setRecordingTime] = useState(0);
  const [stream, setStream] = useState(null);
  const [error, setError] = useState('');
  const [currentView, setCurrentView] = useState('recorder'); // 'recorder' or 'recordings'

  // Refs
  const videoRef = useRef(null);
  const timerRef = useRef(null);

  const MAX_RECORDING_TIME = 180; // 3 minutes in seconds

  // Timer effect
  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => {
        setRecordingTime(prevTime => {
          const newTime = prevTime + 1;
          if (newTime >= MAX_RECORDING_TIME) {
            stopRecording();
            return MAX_RECORDING_TIME;
          }
          return newTime;
        });
      }, 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    }

    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [isRecording]);

  // Cleanup effect
  useEffect(() => {
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [stream]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const startRecording = async () => {
    try {
      // Reset previous recording data
      setRecordedChunks([]);
      setRecordingUrl(null);
      setRecordingTime(0);
      setUploadStatus('');
      setError('');

      // Check browser support
      if (!navigator.mediaDevices || !navigator.mediaDevices.getDisplayMedia) {
        throw new Error('Screen recording is not supported in this browser. Please use Chrome or Edge.');
      }

      // Get display media (screen + audio)
      const displayStream = await navigator.mediaDevices.getDisplayMedia({
        video: { 
          mediaSource: 'screen',
          width: { ideal: 1920, max: 1920 },
          height: { ideal: 1080, max: 1080 },
          frameRate: { ideal: 30, max: 60 }
        },
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          sampleRate: 44100
        }
      });

      let combinedStream = displayStream;

      // Try to get microphone audio and combine with screen audio
      try {
        const micStream = await navigator.mediaDevices.getUserMedia({
          audio: {
            echoCancellation: true,
            noiseSuppression: true,
            autoGainControl: true
          },
          video: false
        });

        // Combine display and microphone streams
        const audioTracks = [
          ...displayStream.getAudioTracks(),
          ...micStream.getAudioTracks()
        ];

        combinedStream = new MediaStream([
          ...displayStream.getVideoTracks(),
          ...audioTracks
        ]);
      } catch (micError) {
        console.warn('Microphone access denied, using only screen audio:', micError);
      }

      setStream(combinedStream);

      // Check MediaRecorder support
      if (!MediaRecorder.isTypeSupported('video/webm;codecs=vp9')) {
        if (!MediaRecorder.isTypeSupported('video/webm')) {
          throw new Error('WebM recording is not supported in this browser.');
        }
      }

      // Create MediaRecorder
      const mimeType = MediaRecorder.isTypeSupported('video/webm;codecs=vp9') 
        ? 'video/webm;codecs=vp9' 
        : 'video/webm';

      const recorder = new MediaRecorder(combinedStream, {
        mimeType,
        videoBitsPerSecond: 2500000, // 2.5 Mbps
        audioBitsPerSecond: 128000   // 128 kbps
      });

      const chunks = [];

      recorder.ondataavailable = (event) => {
        if (event.data && event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      recorder.onstop = () => {
        console.log('Recording stopped, creating blob...');
        const blob = new Blob(chunks, { type: mimeType });
        const url = URL.createObjectURL(blob);
        setRecordingUrl(url);
        setRecordedChunks([blob]);

        // Stop all tracks
        combinedStream.getTracks().forEach(track => {
          track.stop();
          console.log(`Stopped ${track.kind} track`);
        });
        setStream(null);
      };

      recorder.onerror = (event) => {
        console.error('MediaRecorder error:', event.error);
        setError(`Recording error: ${event.error.message}`);
        stopRecording();
      };

      // Handle stream ending (user stops sharing)
      displayStream.getVideoTracks()[0].onended = () => {
        console.log('Screen sharing ended by user');
        if (isRecording) {
          stopRecording();
        }
      };

      // Start recording
      recorder.start(1000); // Collect data every second
      setMediaRecorder(recorder);
      setIsRecording(true);

      // Show live preview
      if (videoRef.current) {
        videoRef.current.srcObject = displayStream;
      }

      console.log('Recording started successfully');

    } catch (error) {
      console.error('Error starting recording:', error);
      setError(`Failed to start recording: ${error.message}`);

      // Cleanup on error
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
        setStream(null);
      }
    }
  };

  const stopRecording = () => {
    console.log('Stopping recording...');

    if (mediaRecorder && mediaRecorder.state === 'recording') {
      mediaRecorder.stop();
    }

    setIsRecording(false);

    // Stop all tracks
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }

    // Reset preview
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  const handleUpload = async () => {
    if (recordedChunks.length === 0) {
      alert('No recording available to upload.');
      return;
    }

    setUploadStatus('uploading');

    try {
      const formData = new FormData();
      const filename = `recording-${Date.now()}.webm`;
      formData.append('video', recordedChunks[0], filename);

      const response = await axios.post(`${API_URL}/api/recordings`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        timeout: 30000, // 30 second timeout
      });

      console.log('Upload successful:', response.data);
      setUploadStatus('success');
      setRefreshKey(prevKey => prevKey + 1);

      // Auto-navigate to recordings view after successful upload
      setTimeout(() => {
        setCurrentView('recordings');
        setUploadStatus('');
      }, 2000);

    } catch (error) {
      console.error("Upload error:", error);
      setUploadStatus('error');

      setTimeout(() => {
        setUploadStatus('');
      }, 5000);
    }
  };

  const downloadRecording = () => {
    if (recordingUrl) {
      const a = document.createElement('a');
      a.href = recordingUrl;
      a.download = `recording-${new Date().toISOString().slice(0, 19).replace(/:/g, '-')}.webm`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
    }
  };

  const clearRecording = () => {
    setRecordingUrl(null);
    setRecordedChunks([]);
    setUploadStatus('');
    setError('');
    setRecordingTime(0);

    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  const progressPercentage = (recordingTime / MAX_RECORDING_TIME) * 100;
  const timeRemaining = MAX_RECORDING_TIME - recordingTime;

  return (
    <Container className="my-4">
      {/* Enhanced Navigation Breadcrumb */}
      <Row className="mb-4">
        <Col>
          <Card className="border-0 shadow-sm">
            <Card.Body className="py-2">
              <Breadcrumb className="mb-0">
                <Breadcrumb.Item 
                  active={currentView === 'recorder'}
                  onClick={() => setCurrentView('recorder')}
                  style={{ cursor: 'pointer' }}
                  className={currentView === 'recorder' ? 'text-primary fw-bold' : ''}
                >
                  🏠 Home
                </Breadcrumb.Item>

                <Breadcrumb.Item>
                  <Dropdown>
                    <Dropdown.Toggle variant="link" className="text-decoration-none p-0 border-0" size="sm">
                      ⋯ Tools
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                      <Dropdown.Item onClick={() => setCurrentView('recorder')}>
                        🎬 Screen Recorder
                      </Dropdown.Item>
                      <Dropdown.Item onClick={() => setCurrentView('recordings')}>
                        📂 My Recordings
                      </Dropdown.Item>
                      <Dropdown.Divider />
                      <Dropdown.Item href="/help" target="_blank">
                        📋 Help & Documentation
                      </Dropdown.Item>
                      <Dropdown.Item href="https://github.com/yourusername/screen-recorder" target="_blank">
                        💻 GitHub Repository
                      </Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                </Breadcrumb.Item>

                <Breadcrumb.Item 
                  active={currentView === 'recordings'}
                  onClick={() => setCurrentView('recordings')}
                  style={{ cursor: 'pointer' }}
                  className={currentView === 'recordings' ? 'text-primary fw-bold' : ''}
                >
                  {currentView === 'recorder' ? '🎬 Screen Recorder' : '📂 My Recordings'}
                </Breadcrumb.Item>
              </Breadcrumb>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Row className="justify-content-center">
        <Col lg={10} xl={8}>
          {/* Main Content - Conditionally render based on current view */}
          {currentView === 'recorder' && (
            <Card className="shadow-lg border-0">
              <Card.Header className="bg-gradient bg-primary text-white text-center py-4">
                <h1 className="mb-2">
                  🎥 Screen Recorder
                  <Badge bg="light" text="primary" className="ms-3 px-3 py-2">
                    MERN Stack
                  </Badge>
                </h1>
                <p className="mb-0 opacity-75">
                  Record your browser screen with audio - Professional quality recordings
                </p>
              </Card.Header>

              <Card.Body className="p-4">
                {/* Quick Actions Toolbar */}
                <div className="d-flex justify-content-between align-items-center mb-4 p-3 bg-light rounded">
                  <div className="d-flex align-items-center">
                    <Badge bg="info" className="me-2 px-3 py-2">
                      📊 Status: {isRecording ? 'Recording' : recordingUrl ? 'Complete' : 'Ready'}
                    </Badge>
                    {recordingTime > 0 && (
                      <Badge bg="secondary" className="px-3 py-2">
                        ⏱️ {formatTime(recordingTime)}
                      </Badge>
                    )}
                  </div>

                  <div className="d-flex gap-2">
                    <Button 
                      variant="outline-primary" 
                      size="sm"
                      onClick={() => setCurrentView('recordings')}
                      className="d-flex align-items-center"
                    >
                      📂 View Recordings
                    </Button>

                    <Dropdown>
                      <Dropdown.Toggle variant="outline-secondary" size="sm">
                        ⚙️ Settings
                      </Dropdown.Toggle>
                      <Dropdown.Menu>
                        <Dropdown.Item onClick={() => console.log('Quality: HD')}>
                          🎯 Recording Quality: HD
                        </Dropdown.Item>
                        <Dropdown.Item onClick={() => console.log('Format: WebM')}>
                          📁 Format: WebM
                        </Dropdown.Item>
                        <Dropdown.Divider />
                        <Dropdown.Item onClick={() => console.log('Help')}>
                          ❓ Help & Support
                        </Dropdown.Item>
                      </Dropdown.Menu>
                    </Dropdown>
                  </div>
                </div>

                {/* Error Display */}
                {error && (
                  <Alert variant="danger" dismissible onClose={() => setError('')} className="border-0 shadow-sm">
                    <Alert.Heading className="h6">🚨 Recording Error</Alert.Heading>
                    <p className="mb-0">{error}</p>
                  </Alert>
                )}

                {/* Recording Timer */}
                {isRecording && (
                  <div className="recording-status mb-4">
                    <Card className="border-danger shadow">
                      <Card.Body className="bg-danger bg-opacity-10">
                        <div className="text-center mb-3">
                          <h3 className="text-danger mb-2 d-flex align-items-center justify-content-center">
                            <span className="recording-pulse me-2">🔴</span>
                            RECORDING: {formatTime(recordingTime)} / {formatTime(MAX_RECORDING_TIME)}
                          </h3>
                          <p className="text-muted mb-0">
                            ⏰ Time remaining: <strong>{formatTime(timeRemaining)}</strong>
                          </p>
                        </div>
                        <ProgressBar 
                          variant={progressPercentage > 80 ? 'danger' : progressPercentage > 60 ? 'warning' : 'success'}
                          now={progressPercentage} 
                          className="mb-2"
                          style={{ height: '12px' }}
                          striped
                          animated
                        />
                        <div className="d-flex justify-content-between small text-muted">
                          <span>0:00</span>
                          <span className="fw-bold">{Math.round(progressPercentage)}% Complete</span>
                          <span>{formatTime(MAX_RECORDING_TIME)}</span>
                        </div>
                      </Card.Body>
                    </Card>
                  </div>
                )}

                {/* Live Preview */}
                {isRecording && (
                  <div className="mb-4">
                    <Card className="border-0 shadow">
                      <Card.Header className="bg-dark text-white">
                        <h5 className="mb-0 d-flex align-items-center">
                          📺 Live Preview
                          <Badge bg="danger" className="ms-auto pulse">● LIVE</Badge>
                        </h5>
                      </Card.Header>
                      <Card.Body className="p-0">
                        <div className="video-container position-relative">
                          <video
                            ref={videoRef}
                            autoPlay
                            muted
                            playsInline
                            className="preview-video w-100"
                            style={{ backgroundColor: '#000' }}
                          />
                          {/* Live indicator overlay */}
                          <div className="position-absolute top-0 start-0 m-3">
                            <Badge bg="danger" className="px-3 py-2 pulse">
                              ● LIVE
                            </Badge>
                          </div>
                        </div>
                      </Card.Body>
                    </Card>
                  </div>
                )}

                {/* Control Buttons */}
                <div className="text-center mb-4">
                  <div className="d-grid gap-3 d-md-flex justify-content-md-center">
                    {!isRecording && !recordingUrl && (
                      <Button
                        variant="success"
                        size="lg"
                        onClick={startRecording}
                        className="px-5 py-3 fw-bold shadow"
                      >
                        🎬 Start Screen Recording
                      </Button>
                    )}

                    {isRecording && (
                      <Button
                        variant="danger"
                        size="lg"
                        onClick={stopRecording}
                        className="px-5 py-3 fw-bold shadow pulse"
                      >
                        ⏹️ Stop Recording
                      </Button>
                    )}
                  </div>
                </div>

                {/* Enhanced Instructions */}
                {!isRecording && !recordingUrl && (
                  <Alert variant="info" className="border-0 shadow-sm">
                    <div className="d-flex align-items-start">
                      <div className="me-3">
                        <div className="bg-info rounded-circle d-flex align-items-center justify-content-center text-white" 
                             style={{ width: '40px', height: '40px', minWidth: '40px' }}>
                          📋
                        </div>
                      </div>
                      <div>
                        <Alert.Heading className="h5 text-info">Getting Started</Alert.Heading>
                        <ol className="mb-0 ps-3">
                          <li className="mb-1"><strong>Click</strong> "Start Screen Recording" button below</li>
                          <li className="mb-1"><strong>Select</strong> the browser tab or entire screen to record</li>
                          <li className="mb-1"><strong>Allow</strong> microphone access for enhanced audio quality</li>
                          <li className="mb-1"><strong>Record</strong> up to 3 minutes (auto-stop feature included)</li>
                          <li className="mb-0"><strong>Manage</strong> your recordings - download locally or upload to cloud</li>
                        </ol>
                      </div>
                    </div>
                  </Alert>
                )}

                {/* Enhanced Recording Preview & Actions */}
                {recordingUrl && !isRecording && (
                  <div className="recording-result">
                    <Card className="border-success shadow">
                      <Card.Header className="bg-success bg-opacity-10 border-success">
                        <h5 className="text-success mb-0 d-flex align-items-center">
                          ✅ Recording Complete!
                          <Badge bg="success" className="ms-auto">Ready for Action</Badge>
                        </h5>
                      </Card.Header>
                      <Card.Body>
                        <div className="video-container mb-4">
                          <video
                            src={recordingUrl}
                            controls
                            className="result-video w-100 rounded shadow"
                            style={{ backgroundColor: '#000' }}
                          />
                        </div>

                        <div className="text-center">
                          <h6 className="text-muted mb-3">Choose your next action:</h6>

                          {/* Primary Actions */}
                          <div className="d-grid gap-2 d-md-flex justify-content-md-center mb-3">
                            <Button 
                              variant="info" 
                              onClick={downloadRecording}
                              className="px-4 py-2 shadow-sm"
                            >
                              📥 Download Locally
                            </Button>

                            <Button
                              variant="primary"
                              onClick={handleUpload}
                              disabled={uploadStatus === 'uploading'}
                              className="px-4 py-2 shadow-sm"
                            >
                              {uploadStatus === 'uploading' ? (
                                <>⏳ Uploading to Cloud...</>
                              ) : (
                                <>☁️ Save to Cloud</>
                              )}
                            </Button>
                          </div>

                          {/* Secondary Actions */}
                          <div className="d-flex justify-content-center gap-2">
                            <Button 
                              variant="outline-secondary" 
                              onClick={clearRecording}
                              size="sm"
                              className="px-3"
                            >
                              🗑️ Clear
                            </Button>

                            <Button 
                              variant="outline-success" 
                              onClick={() => {
                                clearRecording();
                                setTimeout(() => startRecording(), 100);
                              }}
                              size="sm"
                              className="px-3"
                            >
                              🔄 Record Again
                            </Button>

                            <Button 
                              variant="outline-primary" 
                              onClick={() => setCurrentView('recordings')}
                              size="sm"
                              className="px-3"
                            >
                              📂 View All Recordings
                            </Button>
                          </div>
                        </div>
                      </Card.Body>
                    </Card>
                  </div>
                )}

                {/* Enhanced Upload Status */}
                {uploadStatus === 'success' && (
                  <Alert variant="success" className="mt-3 border-0 shadow-sm">
                    <div className="d-flex align-items-center">
                      <div className="me-3">
                        <div className="bg-success rounded-circle d-flex align-items-center justify-content-center text-white" 
                             style={{ width: '40px', height: '40px' }}>
                          ✅
                        </div>
                      </div>
                      <div>
                        <Alert.Heading className="h6 text-success mb-1">Upload Successful!</Alert.Heading>
                        <p className="mb-0">Recording saved to cloud storage. Check your recordings list to view and manage it.</p>
                      </div>
                    </div>
                  </Alert>
                )}

                {uploadStatus === 'error' && (
                  <Alert variant="danger" className="mt-3 border-0 shadow-sm">
                    <div className="d-flex align-items-center">
                      <div className="me-3">
                        <div className="bg-danger rounded-circle d-flex align-items-center justify-content-center text-white" 
                             style={{ width: '40px', height: '40px' }}>
                          ❌
                        </div>
                      </div>
                      <div>
                        <Alert.Heading className="h6 text-danger mb-1">Upload Failed</Alert.Heading>
                        <p className="mb-2">Unable to save recording to cloud storage. Please check your connection and try again.</p>
                        <Button variant="outline-danger" size="sm" onClick={handleUpload}>
                          🔄 Retry Upload
                        </Button>
                      </div>
                    </div>
                  </Alert>
                )}
              </Card.Body>
            </Card>
          )}

          {/* Recordings List View */}
          {currentView === 'recordings' && (
            <div>
              {/* Back to Recorder Button */}
              <div className="mb-3 d-flex justify-content-between align-items-center">
                <Button 
                  variant="outline-primary" 
                  onClick={() => setCurrentView('recorder')}
                  className="d-flex align-items-center"
                >
                  ← Back to Recorder
                </Button>

                <div className="d-flex align-items-center gap-2">
                  <Badge bg="primary" className="px-3 py-2">
                    📊 Dashboard View
                  </Badge>
                </div>
              </div>

              {/* Enhanced Recordings List */}
              <RecordingsList refreshKey={refreshKey} apiUrl={API_URL} />
            </div>
          )}

          {/* Always show recordings list in recorder view */}
          {currentView === 'recorder' && (
            <div className="mt-4">
              <div className="d-flex justify-content-between align-items-center mb-3">
                <h4 className="text-primary mb-0">📂 Recent Recordings</h4>
                <Button 
                  variant="outline-primary" 
                  size="sm"
                  onClick={() => setCurrentView('recordings')}
                >
                  View All →
                </Button>
              </div>
              <RecordingsList refreshKey={refreshKey} apiUrl={API_URL} />
            </div>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default App;
