import React, { useState, useEffect } from 'react';
import { Card, Table, Button, Alert, Badge, Spinner, Modal } from 'react-bootstrap';
import axios from 'axios';

const RecordingsList = ({ refreshKey, apiUrl }) => {
  const [recordings, setRecordings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedRecording, setSelectedRecording] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchRecordings();
  }, [refreshKey, apiUrl]);

  const fetchRecordings = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await axios.get(`${apiUrl}/api/recordings`, {
        timeout: 10000
      });
      setRecordings(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      console.error('Error fetching recordings:', error);
      if (error.code === 'ECONNREFUSED') {
        setError('Cannot connect to server. Please ensure the backend is running on port 5000.');
      } else if (error.response) {
        setError(`Server error: ${error.response.status} - ${error.response.data.message || 'Unknown error'}`);
      } else {
        setError('Failed to load recordings. Please check your connection.');
      }
    } finally {
      setLoading(false);
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getFileName = (filename) => {
    const parts = filename.split('-');
    if (parts.length >= 2) {
      const timestamp = new Date(parseInt(parts[1]));
      if (!isNaN(timestamp.getTime())) {
        return `Recording ${timestamp.toLocaleDateString()} ${timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;
      }
    }
    return filename.replace('.webm', '');
  };

  const playRecording = (recording) => {
    setSelectedRecording(recording);
    setShowModal(true);
  };

  const downloadRecording = (recording) => {
    const url = `${apiUrl}/api/recordings/${recording.id}`;
    const link = document.createElement('a');
    link.href = url;
    link.download = recording.filename;
    link.target = '_blank';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedRecording(null);
  };

  // Calculate total size for footer
  const totalSize = recordings.reduce((sum, recording) => sum + recording.size, 0);

  if (loading) {
    return (
      <Card className="mt-4 shadow">
        <Card.Header className="bg-primary text-white">
          <h4 className="mb-0">🎬 My Screen Recordings</h4>
        </Card.Header>
        <Card.Body className="text-center py-5">
          <Spinner animation="border" role="status" className="mb-3" variant="primary">
            <span className="visually-hidden">Loading...</span>
          </Spinner>
          <p className="text-muted">Loading your recordings...</p>
        </Card.Body>
      </Card>
    );
  }

  return (
    <>
      <Card className="mt-4 shadow border-0">
        <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center">
          <h4 className="mb-0">🎬 My Screen Recordings</h4>
          <div>
            <Badge bg="light" text="primary" className="me-2 px-3 py-2">
              {recordings.length} recording{recordings.length !== 1 ? 's' : ''}
            </Badge>
            <Button 
              variant="outline-light" 
              size="sm" 
              onClick={fetchRecordings}
              disabled={loading}
              className="border-2"
            >
              {loading ? (
                <>
                  <Spinner animation="border" size="sm" className="me-1" />
                  Loading...
                </>
              ) : (
                <>🔄 Refresh</>
              )}
            </Button>
          </div>
        </Card.Header>

        <Card.Body className="p-0">
          {error && (
            <Alert variant="warning" className="m-3 border-0 shadow-sm" dismissible onClose={() => setError(null)}>
              <Alert.Heading className="h6">⚠️ Connection Issue</Alert.Heading>
              <p className="mb-2">{error}</p>
              <Button variant="outline-warning" size="sm" onClick={fetchRecordings}>
                🔄 Try Again
              </Button>
            </Alert>
          )}

          {recordings.length === 0 && !error ? (
            <Alert variant="info" className="m-3 text-center border-0 shadow-sm">
              <div className="py-4">
                <h5 className="text-primary">📹 No recordings yet</h5>
                <p className="text-muted mb-0">
                  Start by recording your screen above, then upload it to see it in this list!
                </p>
              </div>
            </Alert>
          ) : (
            <div className="table-responsive">
              <Table hover className="mb-0 align-middle">
                {/* Table Caption */}
                <caption className="text-center text-muted py-3 border-top">
                  A list of your recent screen recordings.
                </caption>

                {/* Table Header */}
                <thead className="table-dark">
                  <tr>
                    <th className="ps-4" style={{ width: '40%' }}>
                      🎬 Recording
                    </th>
                    <th className="text-center" style={{ width: '15%' }}>
                      📊 Status
                    </th>
                    <th className="text-center" style={{ width: '15%' }}>
                      📏 Size
                    </th>
                    <th className="text-center" style={{ width: '20%' }}>
                      📅 Created
                    </th>
                    <th className="text-end pe-4" style={{ width: '10%' }}>
                      🎮 Actions
                    </th>
                  </tr>
                </thead>

                {/* Table Body */}
                <tbody>
                  {recordings.map((recording) => (
                    <tr key={recording.id} className="border-bottom">
                      <td className="ps-4">
                        <div className="d-flex align-items-center">
                          <div className="me-3">
                            <div className="bg-primary rounded-circle d-flex align-items-center justify-content-center" 
                                 style={{ width: '40px', height: '40px' }}>
                              <span className="text-white fw-bold">🎥</span>
                            </div>
                          </div>
                          <div>
                            <div className="fw-bold text-primary mb-1">
                              {getFileName(recording.filename)}
                            </div>
                            <small className="text-muted">{recording.filename}</small>
                          </div>
                        </div>
                      </td>

                      <td className="text-center">
                        <Badge bg="success" className="px-3 py-2">
                          ✅ Uploaded
                        </Badge>
                      </td>

                      <td className="text-center">
                        <Badge bg="secondary" className="px-3 py-2">
                          {formatFileSize(recording.size)}
                        </Badge>
                      </td>

                      <td className="text-center text-muted">
                        <div className="small">
                          {formatDate(recording.created_at)}
                        </div>
                      </td>

                      <td className="text-end pe-4">
                        <div className="btn-group" role="group">
                          <Button
                            variant="outline-primary"
                            size="sm"
                            onClick={() => playRecording(recording)}
                            title="Play recording"
                            className="border-2"
                          >
                            ▶️
                          </Button>
                          <Button
                            variant="outline-success"
                            size="sm"
                            onClick={() => downloadRecording(recording)}
                            title="Download recording"
                            className="border-2"
                          >
                            📥
                          </Button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>

                {/* Table Footer */}
                {recordings.length > 0 && (
                  <tfoot className="table-light border-top-2">
                    <tr>
                      <td colSpan={4} className="ps-4 fw-bold text-primary">
                        Total Storage Used
                      </td>
                      <td className="text-end pe-4 fw-bold text-primary">
                        {formatFileSize(totalSize)}
                      </td>
                    </tr>
                  </tfoot>
                )}
              </Table>
            </div>
          )}
        </Card.Body>
      </Card>

      {/* Enhanced Video Player Modal */}
      <Modal 
        show={showModal} 
        onHide={closeModal} 
        size="xl" 
        centered
        className="video-modal"
      >
        <Modal.Header closeButton className="bg-dark text-white border-0">
          <Modal.Title className="d-flex align-items-center">
            <span className="me-2">🎬</span>
            {selectedRecording ? getFileName(selectedRecording.filename) : 'Recording'}
          </Modal.Title>
        </Modal.Header>

        <Modal.Body className="p-0 bg-dark">
          {selectedRecording && (
            <div className="position-relative">
              <video
                src={`${apiUrl}/api/recordings/${selectedRecording.id}`}
                controls
                autoPlay
                className="w-100 h-auto"
                style={{
                  maxHeight: '70vh',
                  objectFit: 'contain'
                }}
                onError={(e) => {
                  console.error('Video playback error:', e);
                }}
              >
                Your browser does not support the video tag.
              </video>

              {/* Video overlay info */}
              <div className="position-absolute bottom-0 start-0 end-0 bg-dark bg-opacity-75 text-white p-2">
                <small>
                  <strong>{selectedRecording.filename}</strong> • 
                  {formatFileSize(selectedRecording.size)} • 
                  {formatDate(selectedRecording.created_at)}
                </small>
              </div>
            </div>
          )}
        </Modal.Body>

        <Modal.Footer className="bg-light border-0">
          <div className="d-flex justify-content-between w-100 align-items-center">
            <div className="text-muted small">
              {selectedRecording && (
                <Badge bg="outline-secondary" className="me-2">
                  ID: {selectedRecording.id}
                </Badge>
              )}
              <span>High quality WebM video</span>
            </div>
            <div>
              <Button 
                variant="success" 
                size="sm" 
                onClick={() => selectedRecording && downloadRecording(selectedRecording)}
                className="me-2"
              >
                📥 Download Recording
              </Button>
              <Button variant="outline-secondary" onClick={closeModal}>
                Close Player
              </Button>
            </div>
          </div>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default RecordingsList;
